import React, {useRef} from 'react';
import Input from "../components/Input";

const VerifyPage = () => {
    const codeRef = useRef()
    return (
        <div className="min-h-screen flex items-center justify-center">
            <div className="bg-white p-8 rounded shadow-md w-full max-w-md">
                <h2 className="text-2xl mb-4 text-center">Verification Page</h2>
                <form className="flex flex-col" onSubmit={(e) => e.preventDefault()}>
                    <label className="mb-2 text-gray-700" htmlFor="code">
                        Verification Code:
                    </label>
                    {/*<input*/}
                    {/*    className="px-4 py-2 mb-4 border rounded focus:outline-none focus:border-blue-500"*/}
                    {/*    type="text"*/}
                    {/*    id="code"*/}
                    {/*    name="code"*/}
                    {/*    required*/}
                    {/*/>*/}
                    <Input Ref={codeRef} type={'text'} name={'code'} />

                    <label className="mt-5 text-gray-700" htmlFor="email">
                        Re-send
                    </label>
                    {/*<input*/}
                    {/*    className="px-4 py-2 mb-4 border rounded focus:outline-none focus:border-blue-500"*/}
                    {/*    type="email"*/}
                    {/*    id="email"*/}
                    {/*    name="email"*/}
                    {/*    required*/}
                    {/*/>*/}

                    <button
                        className="bg-blue-500 mt-5 text-white px-6 py-2 rounded hover:bg-blue-700 focus:outline-none"
                        type="submit"
                    >
                        Verify
                    </button>
                </form>
            </div>
        </div>
    );
};

export default VerifyPage;
