// Install Tailwind CSS via npm or yarn if not installed
// npm install tailwindcss
// yarn add tailwindcss

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'
import Axios from 'axios'

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate()
    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            // Send a POST request to the server with username and password
            const response = await Axios.post('http://127.0.0.1:8000/login', {
                username: username,
                password: password,
            });

            // Assuming the server responds with authentication success
            if (response.data.success) {
                // Redirect to '/' after successful login
                navigate('/');
            } else {
                // Handle authentication failure (display an error message, etc.)
                console.log('Authentication failed');
            }
        } catch (error) {
            // Handle errors from the server or network issues
            console.error('Error during login:', error);
        }
    };

    return (
        <div className="bg-gray-100 min-h-screen flex items-center justify-center">
            <div className="bg-white p-8 rounded shadow-md max-w-md w-full">
                <h2 className="text-2xl font-semibold mb-6">Login</h2>
                <form className="space-y-4" onSubmit={handleLogin}>
                    <div className="space-y-2">
                        <label htmlFor="username" className="block text-sm font-medium text-gray-600">
                            Username
                        </label>
                        <input
                            type="text"
                            id="username"
                            name="username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            className="w-full px-4 py-2 border rounded-md"
                        />
                    </div>
                    <div className="space-y-2">
                        <label htmlFor="password" className="block text-sm font-medium text-gray-600">
                            Password
                        </label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="w-full px-4 py-2 border rounded-md"
                        />
                    </div>
                    <div>
                        <button type="submit" className="w-full bg-green-500 text-white py-2 px-4 rounded-md hover:bg-green-600">
                            Login
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Login;
