import React, {useEffect, useRef} from "react";


export const Input = (prop) => {
    const inputRef = useRef()
    useEffect(() => {
        inputRef.current.focus()
    }, []);
    return(
        <input
            //ref={prop.ref || null}
            type={prop.type || 'text'}
            id={prop.id || 'id'}
            name={prop.name || 'name'}
            value={prop.value || ''}
            onChange={prop.onChange}
            className={ prop.className || "w-full px-4 py-2 border rounded-md"}
        />
    )
}
